function removeFrame(){$(".wrapTop").add(".wrapMLeft").add(".wrapMRight").add(".wrapBot").hide()}$(document).ready(function(){var a={Android:function(){return navigator.userAgent.match(/Android/i)},BlackBerry:function(){return navigator.userAgent.match(/BlackBerry/i)},iOS:function(){return navigator.userAgent.match(/iPad/i)},iOSphone:function(){return navigator.userAgent.match(/iPhone|iPod/i)},Opera:function(){return navigator.userAgent.match(/Opera Mini/i)},Windows:function(){return navigator.userAgent.match(/IEMobile/i)},AndroidOnly:function(){return(a.Android()||a.BlackBerry()||a.Opera()||a.Windows())},any:function(){return(a.Android()||a.BlackBerry()||a.iOS()||a.Opera()||a.iOSphone()||a.Windows())}};if(a.any()){removeFrame();$(".wrapContent").removeClass("wrapContent").css({height:"0"});$(".wrapBot").remove(".wrapBot")}else{if(winW>1607){$(".wrapMRight").css("margin-right","-=1")}}});